soup = BeautifulSoup(html_content, 'html.parser')

# Encontramos todas las filas de jugadores en la tabla de goleadores
rows = soup.find_all('div', class_='ui-table__row topScorers__row')

# Iteramos sobre cada fila para extraer la información
Goleadores = {}  # Inicializa el diccionario fuera del bucle

for row in rows:
    # Posición/Ranking del jugador
    rank = row.find('span', class_='topScorers__cell--rank').text.strip()
    
    # Nombre del jugador
    player_name = row.find('div', class_='topScorers__participantCell--player').text.strip()
    
    # Equipo del jugador
    team = row.find('a', class_='topScorers__participantCell').text.strip()
    
    # Goles marcados
    goals = row.find('span', class_='topScorers__cell--goals').text.strip()
    
    # Asistencias (si están presentes, de lo contrario se marca como 'N/A')
    assists = row.find('span', class_='topScorers__cell--gray').text.strip() if row.find('span', class_='topScorers__cell--gray') else 'N/A'
    
    # Agrega la información al diccionario
    Goleadores[player_name] = {'equipo': team, 'goles': int(goals), 'asistencias': int(assists) if assists != 'N/A' else 0}





